<?php if (!defined('THINK_PATH')) exit(); if(is_array($item_list)): $i = 0; $__LIST__ = $item_list;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$item): $mod = ($i % 2 );++$i;?><div class="J_item wall_item"><?php if(isset($like_manage)): ?><a href="javascript:;" class="J_unlike del_item" title="删除" data-id="<?php echo ($item["id"]); ?>"></a><?php endif; if(isset($album_manage)): if($album['uid'] == $visitor['id']): ?><a href="javascript:;" class="J_delitem del_item" title="删除" data-id="<?php echo ($item["id"]); ?>" data-aid="<?php echo ($album["id"]); ?>"></a><?php endif; else: if($item['uid'] == $visitor['id']): ?><a href="javascript:;" class="J_delitem del_item" title="删除" data-id="<?php echo ($item["id"]); ?>"></a><?php endif; endif; ?><!--图片--><ul class="pic"><li><a href="<?php echo U('item/index', array('id'=>$item['id']));?>" target="_blank"><img alt="<?php echo ($item["title"]); ?>" class="J_img" src="<?php echo attach(get_thumb($item['img'], '_m'), 'item');?>"></a><span class="p">¥<?php echo ($item["price"]); ?></span><a href="javascript:;" class="J_joinalbum addalbum_btn" data-id="<?php echo ($item["id"]); ?>"></a></li></ul><!--操作--><div class="favorite"><a href="javascript:;" class="J_likeitem like" data-id="<?php echo ($item["id"]); ?>" <?php if(isset($album)): ?>data-aid="<?php echo ($album["id"]); ?>"<?php endif; ?>><?php echo L('like');?></a><div class="J_like_n like_n <?php if($item['likes'] == 0): ?>hide<?php endif; ?>"><a href="<?php echo U('item/index', array('id'=>$item['id']));?>" target="_blank"><?php echo ($item["likes"]); ?></a><i></i></div><?php if($item['comments'] > 0): ?><span class="creply_n">(<a href="<?php echo U('item/index', array('id'=>$item['id']));?>" target="_blank"><?php echo ($item["comments"]); ?></a>)</span><?php endif; ?><a class="creply" href="<?php echo U('item/index', array('id'=>$item['id']));?>" target="_blank">评论</a></div><!--作者--><?php if(!empty($item['uname'])): ?><div class="author clearfix"><a href="<?php echo U('space/index', array('uid'=>$item['uid']));?>" target="_blank"><img class="J_card avt fl r3" src="<?php echo avatar($item['uid'], '32');?>" data-uid="<?php echo ($item["uid"]); ?>" /></a><div><a href="<?php echo U('space/index', array('uid'=>$item['uid']));?>" class="J_card clr6 bold" target="_blank" data-uid="<?php echo ($item["uid"]); ?>"><?php echo ($item["uname"]); ?></a><br></div></div><?php endif; ?><!--说明--><p class="intro clr6"><?php echo ($item["intro"]); ?></p><!--评论--><?php if(!empty($item['comment_list'])): ?><ul class="rep_list"><?php if(is_array($item['comment_list'])): $i = 0; $__LIST__ = $item['comment_list'];if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$comment): $mod = ($i % 2 );++$i;?><li class="rep_f"><a href="<?php echo U('space/index', array('uid'=>$comment['uid']));?>" target="_blank"><img src="<?php echo avatar($comment['uid'], 24);?>" class="J_card avt fl r3" alt="<?php echo ($comment["uname"]); ?>" data-uid="<?php echo ($comment["uid"]); ?>"></a><p class="rep_content"><a href="<?php echo U('space/index', array('uid'=>$comment['uid']));?>" class="J_card n" target="_blank" data-uid="<?php echo ($comment["uid"]); ?>"><?php echo ($comment["uname"]); ?></a><?php echo ($comment["info"]); ?></p></li><?php endforeach; endif; else: echo "" ;endif; ?></ul><?php endif; ?></div><?php endforeach; endif; else: echo "" ;endif; ?>